var context = $evaluation.getContext();
var permission = $evaluation.getPermission();
var identity = context.getIdentity();
var res = permission.getResource();
print("start");
if(res) {
  print("res: " + res.getName());
  if (identity.id == res.getOwner()) {
    $evaluation.grant();
    print("owner");
  } else {
    $evaluation.deny();
    print("not owner");
  }
} else {
    print("scope only");
    $evaluation.grant();
}
